    <script>
        const paises = [
            "Albania",
            "Andorra",
            "Armenia",
            "Austria",
            "Belgium",
            "Bulgaria",
            "Bosnia and Herzegovina",
            "Belarus",
            "Switzerland",
            "Czech Republic",
            "Germany",
            "Denmark",
            "Estonia",
            "Finland",
            "United Kingdom",
            "Georgia",
            "Greece",
            "Croatia",
            "Hungary",
            "Ireland",
            "Iceland",
            "Italy",
            "Lithuania",
            "Luxembourg",
            "Latvia",
            "Moldova",
            "Macedonia",
            "Montenegro",
            "Norway",
            "Poland",
            "Portugal",
            "Romania",
            "Serbia",
            "Slovakia",
            "Slovenia",
            "Sweden",
            "Turkey",
            "Ukraine",
            "Kosovo",
            "Netherlands",
            "Spain",
            "France",
            "Cyprus"
        ];
        
        // Variables del juego
        let puntuacion = 0;
        let vidas = 5;
        let tiempoRestante = 30;
        let temporizador;
        let paisActual = "";
        let juegoActivo = true;
        let paisesAcertados = [];
        let paisesDisponibles = [];
        let partidaTerminada = false;

        // Elementos del DOM
        const puntuacionDiv = document.getElementById('puntuacion');
        const nomPaisDiv = document.getElementById('nomPais');
        const cuentaAtrasDiv = document.getElementById('cuentaAtras');
        const vidasDiv = document.getElementById('vidas');

        function actualizarPuntuacion() {
            puntuacionDiv.textContent = `Puntuacin: ${puntuacion}`;
        }

        function actualizarVidas() {
            vidasDiv.textContent = `Vidas: ${vidas}`;
        }

        function actualizarTiempo() {
            cuentaAtrasDiv.textContent = `Tiempo: ${tiempoRestante}s`;
        }

        function elegirPaisAleatorio() {
            if (paisesDisponibles.length === 0) {
                finDelJuegoVictoria();
                return;
            }
            
            const indiceAleatorio = Math.floor(Math.random() * paisesDisponibles.length);
            paisActual = paisesDisponibles[indiceAleatorio];
            paisesDisponibles.splice(indiceAleatorio, 1);
            nomPaisDiv.textContent = paisActual;
        }

        function calcularPuntuacionFinal() {
            if (vidas === 5) return Math.floor(puntuacion * 2.5);
            if (vidas === 4) return Math.floor(puntuacion * 2);
            if (vidas === 3) return Math.floor(puntuacion * 1.5);
            if (vidas === 2) return Math.floor(puntuacion * 1.25);
            return puntuacion;
        }

function guardarYRedirigir(tipoFinal) {
    if (partidaTerminada) return;
    partidaTerminada = true;
    juegoActivo = false;
    
    if (temporizador) clearInterval(temporizador);
    
    const puntuacionFinal = calcularPuntuacionFinal();
    
    // El tiempo que dura la partida suele ser (Tiempo Total - Tiempo Restante)
    // Si quieres guardar los segundos que le han sobrado, usa tiempoRestante directamente
    const tiempoJugado = 30 - tiempoRestante;

    console.log('Guardando partida - Puntuacin:', puntuacionFinal, 'Tiempo Jugado:', tiempoJugado);

    // FormData es el formato ms fiable para enviar POST a PHP
    const formData = new FormData();
    formData.append("puntuacion", puntuacionFinal);
    formData.append("tiempo", tiempoJugado);

    // IMPORTANTE: El nombre del archivo ahora coincide exactamente
    fetch("guardarPartida.php", {
        method: "POST",
        body: formData
    })
    .then(response => {
        if (!response.ok) throw new Error('Error en la red');
        return response.json();
    })
    .then(data => {
        console.log('Respuesta del servidor:', data);
        
        if (data.success) {
            console.log("xito! Partida guardada con ID:", data.id_partida);
        } else {
            console.error("Fallo reportado por PHP:", data.error);
        }

        // Redirigir siempre despus de un breve momento
        setTimeout(() => {
            if (tipoFinal === "ganado") {
                window.location.href = "has_ganado.php?puntos=" + puntuacionFinal;
            } else {
                window.location.href = "game_over.php?puntos=" + puntuacionFinal;
            }
        }, 800);
    })
    .catch(error => {
        console.error('Error fatal en fetch:', error);
        
        // Redirigir de todas formas para no bloquear al usuario
        setTimeout(() => {
            if (tipoFinal === "ganado") {
                window.location.href = "has_ganado.php?puntos=" + puntuacionFinal;
            } else {
                window.location.href = "game_over.php?puntos=" + puntuacionFinal;
            }
        }, 800);
    });
}

        function finDelJuegoVictoria() {
            if (!juegoActivo || partidaTerminada) return;
            
            juegoActivo = false;
            clearInterval(temporizador);
            
            const puntuacionFinal = calcularPuntuacionFinal();
            nomPaisDiv.textContent = `VICTORIA! Puntuacin final: ${puntuacionFinal}`;
            
            document.querySelectorAll('path').forEach(path => {
                path.style.pointerEvents = 'none';
            });
            
            // Redirigir despus de un breve retraso para mostrar el mensaje
            setTimeout(() => {
                guardarYRedirigir("ganado");
            }, 1500);
        }

        function finDelJuego() {
            if (!juegoActivo || partidaTerminada) return;
            
            juegoActivo = false;
            clearInterval(temporizador);
            
            const puntuacionFinal = calcularPuntuacionFinal();
            nomPaisDiv.textContent = `GAME OVER! Puntuacin final: ${puntuacionFinal}`;
            
            document.querySelectorAll('path').forEach(path => {
                path.style.pointerEvents = 'none';
            });
            
            // Redirigir despus de un breve retraso para mostrar el mensaje
            setTimeout(() => {
                guardarYRedirigir("perdido");
            }, 1500);
        }

        function finDelJuegoPorTiempo() {
            if (!juegoActivo || partidaTerminada) return;
            
            juegoActivo = false;
            clearInterval(temporizador);
            
            const puntuacionFinal = calcularPuntuacionFinal();
            nomPaisDiv.textContent = `TIEMPO AGOTADO! Puntuacin final: ${puntuacionFinal}`;
            
            document.querySelectorAll('path').forEach(path => {
                path.style.pointerEvents = 'none';
            });
            
            // Redirigir despus de un breve retraso para mostrar el mensaje
            setTimeout(() => {
                guardarYRedirigir("perdido");
            }, 1500);
        }

        function iniciarTemporizador() {
            temporizador = setInterval(() => {
                if (tiempoRestante > 0) {
                    tiempoRestante--;
                    actualizarTiempo();
                } else {
                    finDelJuegoPorTiempo();
                }
            }, 1000);
        }

        function verificarPais(pathElement) {
            if (!juegoActivo || partidaTerminada) return;
            
            const namePais = pathElement.getAttribute('name');
            
            if (namePais === paisActual) {
                puntuacion++;
                actualizarPuntuacion();
                
                pathElement.style.fill = '#90EE90';
                
                if (!paisesAcertados.includes(pathElement.id)) {
                    paisesAcertados.push(pathElement.id);
                }
                
                elegirPaisAleatorio();
                tiempoRestante = 30;
                actualizarTiempo();
            } else {
                const colorOriginal = pathElement.style.fill;
                pathElement.style.fill = '#FF4444';
                
                setTimeout(() => {
                    if (paisesAcertados.includes(pathElement.id)) {
                        pathElement.style.fill = '#90EE90';
                    } else {
                        pathElement.style.fill = colorOriginal;
                    }
                }, 300);
                
                vidas--;
                actualizarVidas();
                
                if (vidas <= 0) {
                    finDelJuego();
                }
            }
        }
    function guardarPartidaEnBDD(puntuacionFinal, tiempoTranscurrido) {
    const formData = new FormData();
    formData.append('puntuacion', puntuacionFinal);
    formData.append('tiempo', tiempoTranscurrido);

    fetch('guardarPartida.php', {
        method: 'POST',
        body: formData
    })
    .then(response => response.json())
    .then(data => {
        if (data.success) {
            console.log("Partida guardada con xito. ID: " + data.id_partida);
            alert("Partida guardada! Puntuacin: " + puntuacionFinal);
        } else {
            console.error("Error al guardar:", data.error);
        }
    })
    .catch(error => console.error('Error en la peticin:', error));
}
        function iniciarJuego() {
            puntuacion = 0;
            vidas = 5;
            tiempoRestante = 30;
            juegoActivo = true;
            partidaTerminada = false;
            paisesAcertados = [];
            paisesDisponibles = [...paises];
            
            const paths = document.querySelectorAll('path');
            paths.forEach(path => {
                path.style.fill = '';
                path.style.pointerEvents = 'auto';
            });
            
            actualizarPuntuacion();
            actualizarVidas();
            actualizarTiempo();
            
            elegirPaisAleatorio();
            
            // Eliminar event listeners anteriores para evitar duplicados
            paths.forEach(path => {
                path.removeEventListener('click', manejarClick);
                path.addEventListener('click', manejarClick);
            });
            
            // Inicializar zoom en el mapa
            const svgElement = document.querySelector('#container svg');
            if (svgElement && typeof svgPanZoom !== 'undefined') {
                svgPanZoom(svgElement, {
                    zoomEnabled: true,
                    controlIconsEnabled: true,
                    fit: true,
                    center: true,
                    minZoom: 0.5,
                    maxZoom: 5,
                    zoomScaleSensitivity: 0.2,
                    dblClickZoomEnabled: false,
                    mouseWheelZoomEnabled: true
                });
            }
            
            if (temporizador) clearInterval(temporizador);
            iniciarTemporizador();
        }

        // Funcin manejadora de clics separada
        function manejarClick(event) {
            event.stopPropagation();
            verificarPais(this);
        }

        document.addEventListener('DOMContentLoaded', iniciarJuego);
    </script>
